	const [todo, setTodo] = useState([{ id: 1, title: "Î¶¨Ïï°Ìä∏ Í≥µÎ∂Ä", content: "Ìà¨ÎëêÎ¶¨Ïä§Ìä∏ ÎßåÎì§Í∏∞", set: false }]);

	const [title, setTitle] = useState("");
	const [content, setContent] = useState("");

	const inputTitleHandler = (event) => {
		setTitle(event.target.value);
	};
	const inputContentHandler = (event) => {
		setContent(event.target.value);
	};

	const todoAddButtonHandler = () => {
		const newTodo = {
			id: Date.now(),
			title,
			content,
			set: false,
		};
		console.log(newTodo);
		setTodo([...todo, newTodo]); //Í∞ùÏ≤¥Î•º ÌíÄÏñ¥ÏÑú ÏßëÏñ¥ÎÑ£Ïñ¥Ïïº ÌïúÎã§.
		setTitle("");
		setContent("");
	};

	return (
		<div className='container'>
			<div className='header'>
				<div>My Todo List</div>
				<div>React</div>
			</div>
			<div className='input-container'>
				<div className='input-content'>
					Ï†úÎ™© : <input type='text' value={title} onChange={inputTitleHandler}></input>
					ÎÇ¥Ïö© : <input type='text' value={content} onChange={inputContentHandler}></input>
				</div>
				<button onClick={todoAddButtonHandler}>Ï∂îÍ∞ÄÌïòÍ∏∞</button>
			</div>
			<div>
				<p>Working...üî•</p>
				<div className='to-do-list'>
					{todo.map((item) => {
						<div key={item.id}>
							<div>{item.title}</div>
							<div>{item.content}</div>
							<div>
								<button>ÏÇ≠Ï†úÌïòÍ∏∞</button>
								<button>ÏôÑÎ£å</button>
