				<div className='done-list'>
					<div>Title</div>
					<div>Content</div>
					<div>
						<button>ÏÇ≠Ï†úÌïòÍ∏∞</button>
						<button>Ï∑®ÏÜå</button>
					</div>
				</div>
			</div>
		</div>
	);
}
export default App;
