import React from "react";
import "./App.css";
import { useState } from "react";

function App() {
	const [todo, setTodo] = useState([{ id: 1, title: "리액트 공부", content: "투두리스트 만들기", set: false }]);

	const [title, setTitle] = useState("");
	const [content, setContent] = useState("");

	const inputTitleHandler = (event) => {
		setTitle(event.target.value);
	};
	const inputContentHandler = (event) => {
		setContent(event.target.value);
	};

	const todoAddButtonHandler = () => {
		const newTodo = {
			id: Date.now(),
			title,
			content,
			set: false,
		};
		console.log(newTodo);
		setTodo([...todo, newTodo]); //객체를 풀어서 집어넣어야 한다.
		setTitle("");
		setContent("");
	};

	const deliteTodoList = () => {
		todo.filter
	}

	return (
		<div className='container'>
			<div className='header'>
				<div>My Todo List</div>
				<div>React</div>
			</div>
			<div className='input-container'>
				<div className='input-content'>
					제목 : <input type='text' value={title} onChange={inputTitleHandler}></input>
					내용 : <input type='text' value={content} onChange={inputContentHandler}></input>
				</div>
				<button onClick={todoAddButtonHandler}>추가하기</button>
			</div>
			<div>
				<p>Working...🔥</p>
				<div className='to-do-list'>
					{todo.map((item) => {
						return (
							<div key={item.id}>
								<div>{item.title}</div>
								<div>{item.content}</div>
								<div>
									<button onClick={deliteTodoList}>삭제하기</button>
									<button>완료</button>
								</div>
							</div>
						);
					}
					)
					}
				</div>
				<p>Done..!🌞</p>
				<div className='done-list'>
					<div>Title</div>
					<div>Content</div>
					<div>
						<button>삭제하기</button>
						<button>취소</button>
					</div>
				</div>
			</div>
		</div>
	);
}
export default App;
