import React from 'react'
import {styled} from 'styled-components'

function Son({props, children}) { //상속받기 위해서 자식인 Son이 props를 넣음.
  console.log(props)
  return <div style={{display : 'flex', gap : '10px', flexWrap : 'wrap'}}>{children}</div>
}
function Son2({props, children}) { //상속받기 위해서 자식인 Son이 props를 넣음.
  console.log(props)
  return <ol> {children}</ol>
}

function Mother(props) { //상속을 받으려는 자식은 props를 넣어줘야 한다. 그럼저기 16번째줄에 있는게 상속됨.
  console.log(props);
  const name = "강수야";
  const hahahahahah = (<>
    <StyledP>나는 {name}의 딸</StyledP>
    <StyledP>그리고 {props.grandFatherName}의 손자</StyledP>
    <StyledP>나는 {name}의 딸</StyledP>
    <StyledP>그리고 {props.grandFatherName}의 손자</StyledP>
    <StyledP>나는 {name}의 딸</StyledP>
    <StyledP>그리고 {props.grandFatherName}의 손자</StyledP>
    <StyledP>나는 {name}의 딸</StyledP>
    <StyledP>그리고 {props.grandFatherName}의 손자</StyledP>
  </>)
  return (<>
  <Son>
    {hahahahahah}</Son>
  <Son2>
    {hahahahahah}</Son2>
  </>); //Son한테는 이걸 상속해준단 뜻.
}

function GrandFather() {
  const name = "강병기";
  return <Mother grandFatherName={name} />;
}

export default GrandFather; //여기는 최상위 컴포넌트와 명이 같아야 한다.

//return문 밑으로는 무조건 최상위 태그가 하나 있어야 한다.
//전역상태관리 props.driling 을 해결해줌
//contestAPI 순정 react에서 만든거 기능이 떨어짐
//redux !!! 헐~ 그렇구나!
//recoil 아항 ㅋㅋ 
//프레그먼트 
const StyledP=styled.p`
width : 200px;
background-color : lightcoral; //세미콜론필수
`
