import React from 'react'
import {useState} from 'react'

function App() {

  const[id, setId] = useState('');
  const[password, setPassword] = useState('');

  console.log('id', id);
  console.log('password',password);


  const onIdChangeHandler = (event)=>setId(event.target.value)
  const onPwChangeHandler = (event)=>setPassword(event.target.value)


  return (
    <div>
      아이디 : <input type="id" value={id} onChange={onIdChangeHandler}/><br/>
      비밀번호 : <input type="password" value={password} onChange={onPwChangeHandler}/><br/>
      <input type="button" value='로그인'onClick={()=>{
        alert(`고객님이 입력하신 아이디는 ${id} 이며, 비밀번호는 ${password}입니다`)
        setId('');
        setPassword('');
        }}/>

    </div>
  )
} 
export default App
