import React from 'react'

function Son({props, children}) { //상속받기 위해서 자식인 Son이 props를 넣음.
  console.log(props)
  return <div>{children}</div>
}

function Mother(props) { //상속을 받으려는 자식은 props를 넣어줘야 한다. 그럼저기 16번째줄에 있는게 상속됨.
  console.log(props);
  const name = "강수야";
  const children = (<div>
    <h1>나는 {name}의 딸</h1>
    <h2>그리고 {props.grandFatherName}의 손자</h2>
  </div>)
  return <Son>{children}</Son>; //Son한테는 이걸 상속해준단 뜻.
}

function GrandFather() {
  const name = "강병기";
  return <Mother grandFatherName={name} />;
}

export default GrandFather; //여기는 최상위 컴포넌트와 명이 같아야 한다.

//return문 밑으로는 무조건 최상위 태그가 하나 있어야 한다.
