import React from 'react'

function Son(props) {
  return <div>나는 {props.motherName}의 아들이에요!</div>;
}

function Mother(props) { //상속을 받으려는 자식은 props를 넣어줘야 한다.
  console.log(props);
  const name = "강수야";
  return <Son motherName={name} />;
}

function GrandFather() {
  const name = "강병기";
  return <Mother grandFatherName={name} />;
}

export default GrandFather; //여기는 최상위 컴포넌트와 명이 같아야 한다.
